import React from "react";

export default function Card({
  headText,
  headSize,
  text,
  spanText,
  link,
  cardClass,
}) {
  let header;

  if (headSize === "1") {
    header = <h1>{headText}</h1>;
  }

  if (headSize === "2") {
    header = <h2>{headText}</h2>;
  }
  console.log(headSize);
  console.log(header);
  return (
    <div className={cardClass}>
      {header}
      <p>
        {text}
        <span> {spanText}</span>
        {"\n"}
        {link}
      </p>
    </div>
  );
}
