import React, { Component } from "react";
import s from "./CookTime.module.css";

export default class CookTime extends Component {
  pauseBtn;
  id;
  prevId;

  state = {
    classNames: `${s.TimeInfo} ${s.Hide}`,
    seconds: 0,
    minutes: this.props.cookTime,
    isPause: false,
    isStart: true,
    cookingTime: 0,
    interval: 0,
  };

  counter = () => {
    // this.setState({
    //   minutes: this.state.cookingTime,
    // });

    if (this.state.seconds == 0) {
      this.setState({
        seconds: 60,
        minutes: this.state.minutes - 1,
      });
    }

    // if (this.state.seconds >= 0 && this.state.seconds <= 9) {
    //   this.setState({
    //     seconds: `0${this.state.seconds}`,
    //   });
    // }

    // if (this.state.cookingTime >= 0 && this.state.cookingTime <= 9) {
    //   this.setState({
    //     minutes: `0${this.state.cookingTime}`,
    //   });
    // }

    if (this.state.minutes === 0 && this.state.seconds === 0) {
      this.setState({
        interval: 0,
      });
    }

    // document.getElementById(`timeInfo${id}`).innerHTML =
    //   minutes + "min : " + seconds + "sec";
    this.setState({
      seconds: this.state.seconds - 1,
    });
  };

  // pauseCounter = () => {
  //   // user can not press pause and continue before start
  //   if (!this.isStart) {
  //     clearInterval(this.interval);
  //     this.isPause = true;
  //   }
  // };

  startTimer = () => {
    this.setState({
      isStart: true,
      interval: clearInterval(this.state.interval),
    });
    setTimeout(() => {
      this.setState({
        classNames: `${s.TimeInfo} ${s.Show}`,
      });

      // this.setState({
      //   minutes: this.props.cookTime,
      //   seconds: 0,
      // });
    }, 1);

    if (this.state.isStart) {
      const interval = setInterval(this.counter, 1000);
      this.setState({
        isStart: false,
        interval: interval,
      });
      console.log(this.state.isStart); // why still true?
      console.log(this.state.interval);
      return this.state.interval;
    }
  };

  // continueCounter = () => {
  //   if (this.isPause) {
  //     clearInterval(this.interval);
  //     this.interval = setInterval(this.counter, 1000);
  //     this.isPause = false;
  //     return this.interval;
  //   }
  // };

  render() {
    const { classNames, minutes, seconds } = this.state;
    const { cookTime } = this.props;
    const timerContent = `${minutes}min : ${seconds}sec`;
    return (
      <div>
        <p>Cook Time: {cookTime} minutes</p>
        <button className={s.Start} onClick={this.startTimer}>
          Start
        </button>
        <button className={s.Pause}>Pause</button>
        <button className={s.Continue}>Continue</button>
        <div className={classNames}>
          <span>{timerContent}</span>
        </div>
      </div>
    );
  }
}
