import React from "react";

export default function Card({
  headText,
  headSize,
  text,
  span,
  link,
  cardClass,
}) {
  let header;

  if (headSize == 1) {
    header = <h1>{headText}</h1>;
  }

  if (headSize == 2) {
    header = <h2>{headText}</h2>;
  }

  return (
    <div className={cardClass}>
      {header}
      <p>
        {text} {span}
        {"\n"}
        {link}
      </p>
    </div>
  );
}
