import React, { Component } from "react";
import Images from "../../assets";
import { Image } from "../ui";
import RecipesData from "../recipes-database/RecipesData";

import s from "./RecipesDetails.module.css";

export default class RecipesDetails extends Component {
  recipes = new RecipesData();

  state = {
    recipe: null,
  };

  componentDidMount() {
    this.updateItem();
  }

  componentDidUpdate(prevProps) {
    if (this.props.itemId !== prevProps.itemId) {
      this.updateItem();
    }
  }

  updateItem() {
    const { itemId } = this.props;
    if (!itemId) {
      return;
    }
    const recipe = this.recipes.getRecipe(itemId);
    this.setState({ recipe });
  }

  render() {
    const { recipe } = this.state;
    if (!recipe) {
      return <span></span>;
    }
    const [{ name, id, yields, cookTime }] = recipe;

    return (
      <div className={s.RecipesDetails}>
        <div className={s.RecipeContent}>
          <h1>{name}</h1>
          <Image src={Images[id]} alt="Recipe Image" />
          <p>Yield: {yields} servings</p>
          <p>Cook Time: {cookTime} minutes</p>
          <button className={s.Start}>Start</button>
          <button className={s.Pause}>Pause</button>
          <button className={s.Continue}>Continue</button>
          <div className={s.TimeInfo}></div>
          <div>
            <h3>Ingredients</h3>
            <ol>
              <li>cup of sugar</li>
              <li>1 tsp of vinegar</li>
              <li>2 tbsp of ketchup</li>
              <li>2 chicken breasts</li>
            </ol>
          </div>
          <div>
            <h3>Instructions</h3>
            <ol>
              <li>mix the sauce ingredients in a large bowl</li>
              <li>add chicken and store in the fridge for at least 1 hour</li>
              <li>pour 1 tbsp of olive oil in a pan</li>
              <li>add chicken and stir-fry until golden brown</li>
            </ol>
          </div>
        </div>
      </div>
    );
  }
}
