import React, { Component } from "react";
import s from "./CookTime.module.css";

export default class CookTime extends Component {
  cookingTime;
  seconds = 0;
  minutes = 0;
  interval;
  pauseBtn;
  isPause = false;
  isStart = true;
  id;
  prevId;

  state = {
    classNames: `${s.TimeInfo} ${s.Hide}`,
  };

  counter = () => {
    this.minutes = this.cookingTime;
    if (this.seconds == -1) {
      this.seconds = 59;
      this.cookingTime--;
    }

    if (this.seconds >= 0 && this.seconds <= 9) {
      this.seconds = `0${this.seconds}`;
    }

    if (this.cookingTime >= 0 && this.cookingTime <= 9) {
      this.minutes = `0${this.cookingTime}`;
    }

    if (this.cookingTime == 0 && this.seconds == 0) {
      clearInterval(this.interval);
    }

    // document.getElementById(`timeInfo${id}`).innerHTML =
    //   minutes + "min : " + seconds + "sec";
    // seconds--;
  };

  pauseCounter = () => {
    // user can not press pause and continue before start
    if (!this.isStart) {
      clearInterval(this.interval);
      this.isPause = true;
    }
  };

  startTimer = () => {
    clearInterval(this.interval);
    this.isStart = true;
    setTimeout(() => {
      this.setState({
        classNames: `${s.TimeInfo} ${s.Show}`,
      });

      this.cookingTime = this.props.cookTime;
      this.seconds = 0;
    }, 1);

    if (this.isStart) {
      this.interval = setInterval(this.counter, 1000);
      this.isStart = false;
      return this.interval;
    }
  };

  continueCounter = () => {
    if (this.isPause) {
      clearInterval(this.interval);
      this.interval = setInterval(this.counter, 1000);
      this.isPause = false;
      return this.interval;
    }
  };
  render() {
    const { classNames } = this.state;
    const { cookTime } = this.props;
    const timerContent = `${this.minutes}min : ${this.seconds}sec`;
    return (
      <div>
        <p>Cook Time: {cookTime} minutes</p>
        <button className={s.Start} onClick={this.startTimer}>
          Start
        </button>
        <button className={s.Pause}>Pause</button>
        <button className={s.Continue}>Continue</button>
        <div className={classNames}>
          <span>{timerContent}</span>
        </div>
      </div>
    );
  }
}
