import React, { Component } from "react";
import s from "./RandomRecipeInfo.module.css";
import { Button, Card } from "../ui";
import { useHistory } from "react-router-dom";

export default class RandomRecipeInfo extends Component {
  routeChange = () => {
    let history = useHistory();
    history.push(`http://localhost:3000/recipes/${this.props.id}`);
  };

  render() {
    const { name, synopsis } = this.props;
    return (
      <div className={s.RandomRecipeInfo}>
        <Card headText={name} headSize="1" text={synopsis} />
        <Button
          id="goToRecipe"
          text="Go to recipe!"
          onClick={this.routeChange}
        />
      </div>
    );
  }
}
