import React from "react";
import s from "./RecipesPage.module.css";
import { RecipesDetails, RecipesList } from "../recipes-page-components";
import { Row } from "../ui";
import { withRouter } from "react-router-dom";

const RecipesPage = ({ history, match }) => {
  const { id } = match.params;
  return (
    <Row
      left={
        <RecipesList
          onItemSelected={(id) => {
            history.push(`${id}`);
          }}
        />
      }
      right={<RecipesDetails itemId={id} />}
    />
  );
};

export default withRouter(RecipesPage);
