import React from "react";
import s from "./RandomRecipeInfo.module.css";
import { Button, Card } from "../ui";
import { Redirect } from "react-router-dom";

export default function RandomRecipeInfo({ name, synopsis, id }) {
  const onClick = () => {
    console.log(id);
    return <Redirect to={`http://localhost:3000/recipes/${id}`} />;
  };

  return (
    <div className={s.RandomRecipeInfo}>
      <Card headText={name} headSize="1" text={synopsis} />
      <Button id="goToRecipe" text="Go to recipe!" onClick={onClick} />
    </div>
  );
}
