import React, { Component } from "react";
import s from "./CookTime.module.css";

export default class CookTime extends Component {
  pauseBtn;
  id;
  prevId;

  state = {
    classNames: `${s.TimeInfo} ${s.Hide}`,
    seconds: 0,
    minutes: this.props.cookTime,
    isPause: false,
    isStart: true,
    cookingTime: 0,
    interval: 0,
  };

  counter = () => {
    if (this.state.minutes === 0 && this.state.seconds === 0) {
      console.log("works");
      this.setState((state) => {
        return {
          interval: clearInterval(state.interval),
        };
      });
    }

    if (this.state.seconds === 0) {
      this.setState({
        seconds: 60,
        minutes: this.state.minutes - 1,
      });
    }

    // document.getElementById(`timeInfo${id}`).innerHTML =
    //   minutes + "min : " + seconds + "sec";
    this.setState({
      seconds: this.state.seconds - 1,
    });
  };

  // pauseCounter = () => {
  //   // user can not press pause and continue before start
  //   if (!this.isStart) {
  //     clearInterval(this.interval);
  //     this.isPause = true;
  //   }
  // };

  componentDidMount() {
    console.log("did mount");
  }

  componentWillUnmount() {
    console.log("will unmount");
  }

  startTimer = () => {
    this.setState((state) => {
      return {
        isStart: !state.isStart,
        interval: clearInterval(state.interval),
      };
    });

    setTimeout(() => {
      this.setState({
        classNames: `${s.TimeInfo}`,
      });

      this.setState({
        minutes: this.props.cookTime,
        seconds: 0,
      });
    }, 1);

    if (this.state.isStart) {
      this.setState((state) => {
        return {
          isStart: !state.isStart,
          interval: setInterval(this.counter, 1000),
        };
      });
      return this.state.interval;
    }
  };

  // continueCounter = () => {
  //   if (this.isPause) {
  //     clearInterval(this.interval);
  //     this.interval = setInterval(this.counter, 1000);
  //     this.isPause = false;
  //     return this.interval;
  //   }
  // };

  render() {
    const { classNames, minutes, seconds } = this.state;
    const { cookTime } = this.props;
    let minutesFormatted = minutes;
    let secondsFormatted = seconds;

    if (minutes >= 0 && minutes <= 9) {
      minutesFormatted = `0${minutes}`;
    }

    if (seconds >= 0 && seconds <= 9) {
      secondsFormatted = `0${this.state.seconds}`;
    }

    const timerContent = `${minutesFormatted}min : ${secondsFormatted}sec`;
    return (
      <div className={s.cookTime}>
        <p>Cook Time: {cookTime} minutes</p>
        <button className={s.Start} onClick={this.startTimer}>
          Start
        </button>
        <button className={s.Pause}>Pause</button>
        <button className={s.Continue}>Continue</button>
        <div className={classNames}>
          <span>{timerContent}</span>
        </div>
      </div>
    );
  }
}
